error.pattern:
// BinaryExpr [ @Op = KTC_OPCODE_LT |
                @Op = KTC_OPCODE_GT |
                @Op = KTC_OPCODE_LE |
                @Op = KTC_OPCODE_GE ]
[ Left.getTypeName() = 'BSTR' |
Right.getTypeName() = 'BSTR' ]

// UnaryExpr [ @Op = KTC_OPCODE_PREINC |
               @Op = KTC_OPCODE_PREDEC |
               @Op = KTC_OPCODE_POSTINC |
               @Op = KTC_OPCODE_POSTDEC ]
             [ Expr.getTypeName() = 'BSTR' ]

// CallExpr [ getName() = 'SysReAllocString' | getName() = 'SysReAllocStringLen' ] 
            [ $0 := getName() ]
            [ not Args[0]::* [ isPointerToBSTR() ] ]

// BinaryExpr [ @Op = KTC_OPCODE_ANDASSIGN 
              | @Op = KTC_OPCODE_XORASSIGN 
              | @Op = KTC_OPCODE_ORASSIGN 
              ] 
              [ not Right:: { ParensExpr / Expr:: } LiteralExpr ]
              [ Left.getTypeSize() > Right.getTypeSize() ]

// UnaryExpr [ @Op = KTC_OPCODE_PREINC | 
               @Op = KTC_OPCODE_POSTINC | 
               @Op = KTC_OPCODE_PREDEC | 
               @Op = KTC_OPCODE_POSTDEC ]
             [ Expr.getTypeName() = 'bool' ]

// MemberDecl [ Declarators[*]::BitFieldDeclarator [ Bits::LiteralExpr [ getIntValue() = 1 ] ] 
                                                   [ $0 := getName() ]
                                                   [ Declarator::AnyDeclarator.isSigned() ] ]

// BinaryExpr [ @Op = KTC_OPCODE_ADD |
                @Op = KTC_OPCODE_ADDASSIGN |
                @Op = KTC_OPCODE_SUB |
                @Op = KTC_OPCODE_SUBASSIGN
              ]
              [ $ltype := Left.getLanguageType().getInitialDefinedType() ]
              [ $ltype.isPointer() ]
              [ $pltype := $ltype.getPointedType().getInitialDefinedType() ]
              [ $pltype.getTypeName() != 'void' ]
              [ $pltype.getTypeName() != 'char' ]
              [ Right::SizeOfExpr [ $sotype := Type.getSemanticInfo().getInitialDefinedType() ] 
                                  [ $sotype.isPointer() | $sotype = $pltype ]
              ]
              [ $0 := Left.pretty() ]

// BinaryExpr [ @Op=KTC_OPCODE_LT | 
                @Op=KTC_OPCODE_GT | 
                @Op=KTC_OPCODE_LE | 
                @Op=KTC_OPCODE_GE |
                @Op=KTC_OPCODE_EQ |
                @Op=KTC_OPCODE_NE ]
              [ Left.getInitialDefinedType().getTypeName() = 'char' ]
              [ not Left::LiteralExpr ]
              [ not Right::LiteralExpr ]
              [ $right := Right.getInitialDefinedType().getTypeName() ]
              [ $right = 'signed char' | $right = 'unsigned char' ]

// BinaryExpr [ @Op=KTC_OPCODE_LT | 
                @Op=KTC_OPCODE_GT | 
                @Op=KTC_OPCODE_LE | 
                @Op=KTC_OPCODE_GE |
                @Op=KTC_OPCODE_EQ |
                @Op=KTC_OPCODE_NE ]
              [ Right.getInitialDefinedType().getTypeName() = 'char']
              [ not Right::LiteralExpr ]
              [ not Left::LiteralExpr ]
              [ $left := Left.getInitialDefinedType().getTypeName() ]
              [ $left = 'signed char' | $left = 'unsigned char' ]

// CastExpr [ Type::TypeId [ $isok := getInitialDefinedType().isPointer() ] ]
            [ Expr::AnyExpr [ $isok != getInitialDefinedType().isPointer() ] 
                            [ name() != 'LiteralExpr' | getIntValue() != 0 ] ]

// InitializedDeclarator
     [ Init::CopyInitializer/Expr::AnyExpr [ getUnderlyingType().getTypeName() != 'char' ] 
                                           [ not isCharacterLiteral() ]
     ]
     [ getUnderlyingType().getTypeName() = 'char' ]

// UnaryExpr [ @Op = KTC_OPCODE_PREINC |
               @Op = KTC_OPCODE_PREDEC |
               @Op = KTC_OPCODE_POSTINC |
               @Op = KTC_OPCODE_POSTDEC ]
             [ Expr.getUnderlyingType().getTypeName() = 'char' ]
             [ not isOverloaded() ]

// LabeledStmt [ Label::CaseLabel [ Expr.getUnderlyingType().getTypeName() != 'char' ] 
                                  [ not Expr.isCharacterLiteral() ] 
               ]
               [ parent:: { LabeledStmt / parent:: } CompoundStmt 
                                   / parent::SwitchStmt 
                                              [ Cond.getUnderlyingType().getTypeName() = 'char' ] ]

// IfStmt [ isCLanguage() ] 
          / Cond::* [ not isBoolean() ]
                    [ not getTypeName().contains-nocase('bool') ]

// DoStmt [ isCLanguage() ]
          / Cond::* [ not isBoolean() ]
                    [ not getTypeName().contains-nocase('bool') ]

// ForStmt [ isCLanguage() ]
           / Cond::* [ not isBoolean() ]
                     [ not getTypeName().contains-nocase('bool') ]

// WhileStmt [ isCLanguage() ] 
             / Cond::* [ not isBoolean() ]
                       [ not getTypeName().contains-nocase('bool') ]

// BinaryExpr [ @Op = KTC_OPCODE_ASSIGN ]
              [ Left::MemberExpr / Expr:: { MemberExpr / Expr:: } AnyExpr [ isUnionType() ] 
                                                                          [ $left := getSemanticInfo() ]
              ]
              [ Right::MemberExpr / Expr:: { MemberExpr / Expr:: } AnyExpr [ isUnionType() ] 
                                                                           [ getSemanticInfo() = $left ]
              ]

// CastExpr [ isCLanguage() ]
            [ Expr::AnyExpr 
              [ getInitialDefinedType().isPointer() ] 
              [ $0 := getTypeName() ]
            ]
            [ Type::TypeId
              [ not getInitialDefinedType().isPointer() ] 
              [ not getInitialDefinedType().isIntegral() ] 
              [ $1 := getTypeName() ]
            ]

// ForStmt [ isCLanguage() ] 
           [ Init::Decl / Declarators[*]::AnyDeclarator [ $name := getName() ] ]
           [ Cond::AnyExpr [ not descendant::IdExpr [ getName() = $name ] ] ]

// IfStmt [ parent::IfStmt ]
          [ Else::Null 
          | Else::ExprStmt / Expr::Null 
          | Else::CompoundStmt [ not containsComments() ] 
                               [ not descendant::* [ name() != 'ExprStmt' | not Expr::Null ] ]
          ]

// EnumType [ Enumerators[0]::Enumerator [ Expr::* ]
                / following-sibling::Enumerator [ $0 := getName() ] [ Expr::* ] ]
            [ Enumerators[*]::Enumerator [ $1 := getName() ] [ Expr::Null ] ]

// FuncDeclarator [ isCLanguage() ]
                  [ Params[*]::Decl [ DeclSpecs::BuiltinType [ @Spec = KTC_BUILTINTYPE_VOID ] ] 
                                    [ Declarators[*]::PtrDeclarator [ getName() = '' ] ]
                  ]  
                  [ not ancestor::FuncDef ]

// AsmStmt  [ not comesFromMacro() ]
            [ ancestor::FuncBody / Stmt::CompoundStmt 
              [ Stmts[*]::Decl
                | Stmts[*]::ExprStmt
            ] ]

// ParensExpr [ Expr::CastExpr ]
              [ not comesFromMacro() ]
              [ not parent::MemberExpr ]

// CastExpr [ Expr::ParensExpr 
              [ not (Expr::BinaryExpr | Expr::ConditionalExpr) ]
              [ not comesFromMacro() ]
            ]

// SpecialCastExpr [ not isCLanguage() ]
                   [ Type::TypeId [ getInitialDefinedType().isIntegral() ] ]
                   [ Expr::AnyExpr [ isCValue() ] 
                                   [ isFloatPoint() ]
                   ]

// SpecialCastExpr [ not isCLanguage() ]
                   [ Type::TypeId [ getInitialDefinedType().isFloatPoint() ] ]
                   [ Expr::AnyExpr [ isCValue() ] 
                                   [ isIntegral() ]
                   ]

// ForStmt [ not isCLanguage() ]
           [ Cond::AnyExpr [ $type := getUnderlyingType() ]
                           [ not $type.isBoolean() ] 
           ]
           [ $0 := $type.getTypeName() ]

// IfStmt [ not isCLanguage() ]
          [ Cond::AnyExpr [ $type := getUnderlyingType() ]
                          [ not $type.isBoolean() ]
          ]
          [ $0 := $type.getTypeName() ]

// WhileStmt [ not isCLanguage() ]
             [ Cond::AnyExpr [ $type := getUnderlyingType() ]
                             [ not $type.isBoolean() ]
             ]
             [ $0 := $type.getTypeName() ]

// TemplateDecl [ not isCLanguage() ]
                [ parent::TranslationUnit ]
                [ not getParent() | isGlobal() ]

// ExplicitInstantiation [ not isCLanguage() ]
                         [ parent::TranslationUnit ]
                         [ not getParent() | isGlobal() ]

// CallExpr [ not isCLanguage() ]
            [ Func::IdExpr [ getName() = 'terminate' ] ]
            [ Args::Null ]

// MemberDecl [ not isCLanguage() ]
              [ not DeclSpecs[*]::FuncSpec [ @Spec = KTC_FUNCSPECIFIER_VIRTUAL ] ]
              / Declarators[0]::AnyDeclarator [ isVirtual() ]

// MemberFunc [ not isCLanguage() ]
              [ isVirtual() ]
              [ not DeclSpecs[*]::FuncSpec [ @Spec = KTC_FUNCSPECIFIER_VIRTUAL ] ]

// ConditionalExpr
        [ Else::AnyExpr
            [ isPointer() ]
        ]
        [ Then::AnyExpr
            [ getIntValue() = 0 ]
            [ pretty() != 'NULL']
        ]
        [ not Then:: { ParensExpr / Expr:: } CastExpr
            [ Type::TypeId [ getInitialDefinedType().isPointer() ] ]
            [ getPointedType().getInitialDefinedType().getTypeName() = 'void' ]
            [ Expr:: { ParensExpr / Expr:: } LiteralExpr ]
        ]

// ConditionalExpr
        [ Then::AnyExpr
            [ isPointer() ]
        ]
        [ Else::AnyExpr
            [ getIntValue() = 0 ]
            [ pretty() != 'NULL']
        ]
        [ not Else:: { ParensExpr / Expr:: } CastExpr
            [ Type::TypeId [ getInitialDefinedType().isPointer() ] ]
            [ getPointedType().getInitialDefinedType().getTypeName() = 'void' ]
            [ Expr:: { ParensExpr / Expr:: } LiteralExpr ]
        ]
